//Added distance formula for points in the XY plane
function distance_formula(x1, y1, z1, x2, y2, z2){
  return Math.sqrt(Math.pow(x2-x1, 2) + Math.pow(y2-y1, 2) + Math.pow(z2-z1, 2));
}

var distance = function (gt, ex1) {
    // var difference = x - y;
    var difference = distance_formula(gt.X, gt.Y, gt.Z, ex1.X, ex1.Y, ex1.Z);
    var squaredEuclideanDistance = difference * difference;
    return squaredEuclideanDistance;
};

module.exports = {
    distance: distance
};
